import React, {Component} from 'react'
import axios from 'axios'

export default class Posts extends Component {
	//
	constructor(props) {
		super(props)

		this.onChangeSearch = this.onChangeSearch.bind(this)
		this.onSubmit = this.onSubmit.bind(this)

		this.state = {
			queryString: "houston #dogs"
		}
	}

	onChangeSearch(e) {
		// this.setState({
		// 	queryString: e.target.value
		// })

		let query = e.target.value
		//query = query.split(" ")
		console.log(query)

		// need to prep the query, make into pretty json format n stuff

		let len = query.length 
		//while(len !== 0){
		if(query[0] === '#')
			console.log(query[len-1] + ' is a hashtag')
		else 
			console.log('pick as hashtag, location or username')
		//len--
		//}
		// run search 


		// need to track whats a hashtag and whats not
		// if all that remains are astags display results
		// if not a hastag, display locations and users matching the word & hashtag!
		// there's no location or username models in db yet, so just display it lol
		// add hashtags, location, user in state
	}

	onSubmit(e) {
		e.preventDefault()

		let query = ""; // build from state

		axios.get('http://localhost:4000/posts/search/' + query)
            .then(res => console.log(res.data))

		this.setState({
			queryString: '#cute #cats'
		})
	} 
	// 

	render() {
		return (
			<div>
				<form onSubmit={this.onSubmit}>
					<div>
						<label>search form:</label>
						<input
							type="text"
							value={this.state.queryString}
							onChange={this.onChangeSearch}
							/>
					</div>
					<div>
						<input type="submit" value="Submit"/>
					</div>
				</form>
			</div>
		)
	}

}
